import React from "react";

// also in the HTML version
let transactionState = 0; //how much money we're trying to withdraw or deposit
let totalState = 0; //running total of how much we have in our account
let status = "Account Balance $zero";

// compared to the HTML version, handleChange notices the manual input, too and console.logs handleChange + value (number)
const handleChange = event => {
  console.log(`handleChange ${event.target.value}`);
  transactionState = Number(event.target.value); //this is a change
};

// using the totalState variable only here
// using the transactionState variable only here
const handleSubmit = event => {
  totalState = totalState + transactionState;
  status = `Account Balance $ ${totalState}`;
  document.getElementById("total").innerHTML = status;
  event.preventDefault();
};

const props  = {handleChange, handleSubmit}

const ATMDeposit = ({ onChange }) => {
  return (
    <label className="label huge">
      Deposit:
      <input type="number" onChange={handleChange}></input>
      <input type="submit" onClick={handleSubmit}></input>  
    </label>
  );
};


const Account = () => {
}

export default Account;
