import React, {useState, createContext, useContext} from "react";
import Card from "./Card";

function CreateAccount() {
    const [show, setShow] = useState(true);
    const [status, setStatus] = useState("");
    let [name, setName] = useState("");
    let [email, setEmail] = useState("");
    let [password, setPassword] = useState("");
    let [nameError, setNameError] = useState("");
    let [emailError, setEmailError] = useState("");
    let [passwError, setPasswError] = useState("");


    const MyContext = createContext(null); //creates a Context object
    const context = useContext(MyContext);  

    //validation, given code
    // const validate = (fieldName, statusText) => {
    //     if (!fieldName) {
    //         setStatus("Error: " + statusText);
    //         setTimeout(() => setStatus(""), 3000); //why is setStatus("") called with an empty string?
    //         return false; //validation is false
    //     }
    //     return true;  //validation is true
    // }

    const validate = () => {
        // setNameError("");
        // setEmailError("");
        // setPasswError("");

        let nameErrorStr = "";
        let emailErrorStr = "";
        let passwErrorStr = "";

        if(!name) {
            nameErrorStr = "Please, enter name";
            console.log("where's the name")

        }

        if(!email) {
            emailErrorStr = "Please, enter email address"
            console.log("where's the email")

        }

        if(!password) {
            passwErrorStr = "Please, enter password"
            console.log("where's the password")
        }

        if(nameErrorStr || emailErrorStr || passwErrorStr) {
            setNameError(nameErrorStr);
            console.log("name error string: " + nameErrorStr)
            setEmailError(emailErrorStr);
            console.log("name error string: " + emailErrorStr)
            setPasswError(passwErrorStr);
            console.log("name error string: " + passwErrorStr)
            return false;
        }
        return true;
    }

    //validation
    //need to further check this
    const handleCreate = e => {
        // console.log(name, email, password);

        if(!validate()) {
            return;
        }

        //lenghty version
        // if (!validate(name, 'Please enter name')) {
        //     return;
        // } else if(!validate(email, 'Please enter email address')) {
        //     return;
        // } else if(!validate(password, 'Please enter password')) {
        //     return;
        // }

        //short version
        // if (!validate(name, 'Please enter name')) return;
        // if (!validate(email, 'Please enter email address')) return;
        // if (!validate(password, 'Please enter password')) return;



        // context.users.push({name, email, password, balance:100});
        // ez kell
        // context.users.push({name, email, password});
        // setShow(false);
    }    
    //when all 3 pass validate, it crashes


    //form input 
    //can't have same function for all 3 as in helper code
    const handleNameChange = e => {
        setName(e.currentTarget.value);
    }

    const handleEmailChange = e => {
        setEmail(e.currentTarget.value);
    }

    const handlePasswordChange = e => {
        setPassword(e.currentTarget.value);
    }


    const clearForm = () => {
        setName("")
        setEmail("")
        setPassword("")
        setShow(true);
    }

    return (
        <Card
            bgcolor="info"
            header="Open Account"
            status={status}
            body={show ? (  
                <>
                    <h5 className="create mb-3">Open an account that fits your needs!</h5>
                    <h6>Name</h6>
                    <input type="input" className="form-control" id="name" placeholder="Enter name here" value={name} onChange={handleNameChange} /><br/>
                    <div className="error">{nameError}</div>

                    <h6>Email address</h6>
                    <input type="input" className="form-control" id="email" placeholder="Enter email here" value={email} onChange={handleEmailChange}/><br/>
                    <div className="error">{emailError}</div>

                    <h6>Password</h6>
                    <input type="password" className="form-control" id="password" placeholder="Enter password here" value={password} onChange={handlePasswordChange}/><br/>
                    <button type="submit" className="btn btn-light" onClick={e => handleCreate()}>Create Account</button>
                    <div className="error">{passwError}</div>
                    </>
                    ):(
                    <>
                    <h6>Success</h6>
                    <button type="submit" className="btn btn-light" onClick={clearForm}>Add another account</button>
                </>
            )}
        />
    )
}

export default CreateAccount;
