import React from 'react';
import Card from "./Card";
import CreateAccount from "./CreateAccount";

/*

The Withdraw page includes a Bootstrap card with the following: 
- Withdraw input field on a Bootstrap card: ok
- Submit button on a Bootstrap card: ok
- Balance information displayed above the Withdrawal form: UI ok

Not done (check all functionality):
  - submit button function
  - current balance numbers
  - input validation
  - etc <3 

-
*/

function Withdraw() {
  return (
    <Card
        bgcolor="info"
        header="Withdraw"
        status={CreateAccount.status}
        body={
            <>  
              <h5 className="deposit mb-4">Skip extra trips to the bank and withdraw your money online!</h5>
              <h6 className="balance mb-4">Your current balance is: ${}</h6>
              <h6>The amount you wish to withdraw:</h6>
              <input type="input" className="form-control" id="email" placeholder="Amount" value={CreateAccount.email} onChange={CreateAccount.email}/>
              <br/>
              <button type="submit" className="submitBtn btn btn-light" onClick={CreateAccount.email}>Submit</button>
            </>
        }
    />
  )
}

export default Withdraw;
