import React, {useContext} from "react";
import { MyContext } from "./MyContext";
import Card from "./Card";


function Withdraw() {
  const {
    //state:
    name,
    email,
    password,
    nameError,
    emailError,
    passwError,
    depositAmount,
    balance,
    numberError,
    successMessageVisible,
    //functions:
    validate,
    handleClick,
    handleNameChange,
    handleEmailChange,
    handlePasswordChange,
    clearForm,
    validateDep,
    handleChange,
    submitDeposit,
    showHideSuccessMessage
  } = useContext(MyContext);



  return (
    <Card
        bgcolor="info"
        header="Withdraw"
        body={
            <>  
              <h5 className="deposit mb-4">Skip extra trips to the bank and withdraw your money online!</h5>
              <h6 className="balance mb-4">Your current balance is: ${balance}</h6>
              <h6>The amount you wish to withdraw:</h6>
              <input type="input" className="form-control" id="email" placeholder="Amount" onChange={handleChange}/>
              <div>
                <div className="error mb-3">{numberError}</div>
              </div>
              <br/>
              <button type="submit" disabled={!withdrawAmount} className="submitBtn btn btn-light" onClick={() => submitDeposit(depositAmount)}>Withdraw</button>
              {}
            </>
        }
    />
  )
}

export default Withdraw;
