            {/* this has no sense because I'm not getting the data in JSON */}
            {/* /* <div className='json'> */}

              {/* {JSON.stringify(context.name, null, 2)}
              <br/>
              {JSON.stringify(context.email, null, 2)}
              <br/>

              {JSON.stringify(context.password, null, 2)}
              <br/>

              {JSON.stringify(context.depositAmount, null, 2)}
              <br/>

              {JSON.stringify(context.withdrawAmount, null, 2)}
              <br/>

              {JSON.stringify(context.balance, null, 2)} */}
            {/* </div>  */}
          </>
      }
    />
  );
}



export default AllData;
