import React, { createContext, useContext } from 'react';
import { MyContext} from "./MyContext";
import Card from "./Card";

function AllData() {

  const {
    //state:
    name,
    email,
    password,
    nameError,
    emailError,
    passwError,
    depositAmount,
    balance,
    numberError,
    successMessageVisible,
    withdrawSuccessMessageVisible,
    withdrawAmount,
    withdrawError,
    //functions:
    validate,
    handleClick,
    handleNameChange,
    handleEmailChange,
    handlePasswordChange,
    clearForm,
    validateNr,
    handleChange,
    submitDeposit,
    showHideSuccessMessage,
    withdrawHandleChange,
    submitWithdrawal,
    showHideSuccessMessageWithdraw
  } = useContext(MyContext);

  const UserContext = createContext(null);

  const ctx = useContext(UserContext);

  return (
    <Card
      bgcolor="info"
      header="All Data"
      body={
          <>  
            <h5 className="balance mb-5">All Your Data in one spot!</h5>
            <div className="alldata">
              {/* <div className="title">Personal Information</div>
              <h6>Name: {name}</h6>
              <h6>Email address: {email}</h6>
              <h6>Password: {password}</h6>
              <br />
              <div className="title">Past Transactions</div>
              <h6>Deposited amount: ${depositAmount}</h6>
              <h6>Withdrawn amount: ${withdrawAmount}</h6>
              <h6>Current balance: ${balance}</h6> */}
              {JSON.stringify(ctx)}
            </div>     
          </>
      }
    />
  );
}


export default AllData;
