import React, {useState} from "react";
import Card from "./Card";
import CreateAccount from "./CreateAccount";


function Deposit() {
  let [depositNr, setDepositNr] = useState("");
  let [numberError, setNumberError] = useState("");

  //error string
  let numberErrorStr = "";

  const validate = () => {
    setNumberError("");
  console.log(depositNr)
    //number format function
    let isValidFormat = nrs => {
      let depositFormat = /^[.0-9\b]+$/;
      return nrs.match(depositFormat);
    }

    if(!isValidFormat(depositNr)) {
      numberErrorStr = "Enter only numbers";
      setNumberError(numberErrorStr);
    }
  }

  const handleChange = e => {
    setDepositNr(e.currentTarget.value)
    validate()
  }





  return (
    <Card
        bgcolor="info"
        header="Deposit"
        // status={CreateAccount.status}
        body={
          <>  
            <h5 className="deposit mb-4">Skip extra trips to the bank and deposit your money online!</h5>
            <div>
              <div className="error">{numberError}</div>
            </div>

            <h6 className="deposit mb-4">Your current balance: ${}</h6>

            <h6>The amount you wish to deposit:</h6>

            <input type="input" className="form-control" id="depositNr" placeholder="Amount" value={depositNr} onChange={handleChange}/>
            <br/>
            <button type="submit" className="btn btn-light" onClick={CreateAccount.email}>Submit</button>
          </>
        }
    />
  )
}

export default Deposit;
