import React, {useState} from 'react';
// import Tooltip from 'react-tooltip';
// import "./index.css"

function NavBar() {

  const [activePage, setActivePage] = useState("");
  const handleClick = (e) => {
    setActivePage(e.currentTarget.dataset.index);
  }

  const getClass = (classIndex) => {
    console.log(classIndex + " " + activePage + " ")
    if (classIndex == activePage) {
    }
    return ""
  }

  return (
    <>    
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <a className="navbar-brand" href="#">Welcome to Bad Bank</a>

      {/* BUTTON */}
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        {/* hamburger icon */}
        <span className="navbar-toggler-icon"></span> 
      </button>

      {/*  main container: containing nav items from item 1 till length of page */}
      <div className="collapse navbar-collapse nav-container" id="navbarNav">

        {/* container containing nav items only */}
        <ul className="navbar-nav navitem-container">

        {/* nav items: */}
          <li data-index="0" className={`nav-item ${getClass(0)}`} onClick={handleClick}>
            <a className="nav-link pl-2 pr-2" href="#/CreateAccount/">Create Account</a>
          </li>

          <li data-index="1" className={`nav-item ${getClass(1)}`} onClick={handleClick}>
            <a className="nav-link pl-2 pr-2" href="#/login/">Login</a>
          </li>

          <li data-index="2" className={`nav-item ${getClass(2)}`} onClick={handleClick}>
            <a className="nav-link pl-2 pr-2" href="#/deposit/">Deposit</a>
          </li>

          <li data-index="3" className={`nav-item ${getClass(3)}`} onClick={handleClick}>
            <a className="nav-link pl-2 pr-2" href="#/withdraw/">Withdraw</a>
          </li>
          
          <li data-index="4" className={`nav-item ${getClass(4)}`} onClick={handleClick}>
            <a className="nav-link pl-2 pr-2" href="#/balance/">Balance</a>
          </li>

          <li data-index="5" className={`nav-item ${getClass(5)}`} onClick={handleClick}>
            <a className="nav-link pl-2 pr-2" href="#/alldata/">All Data</a>
          </li>          
        </ul>
        {/* end of container for nav items */}
      </div>
      {/* end of main container */}
    </nav>
    </>
  )
}


export default NavBar;
