import React, { createContext, useContext } from 'react';
import {MyContext, MyContextProvider} from "./MyContext";
import Card from "./Card";


function AllData() {

  const {name, email, password, depositAmount, withdrawAmount, balance, users} = useContext(MyContext);

  console.log(users)
  return (
    <Card
      bgcolor="info"
      header="All Data"
      body={
          <>  
            <h5 className="balance mb-2">All Your Data in one spot!</h5>
            <div className="alldata">
            <div className="title">Your accounts and transactions</div>
              {users.map((user) => {
                  return (                   
                    <div className="title">Account:
                    <div className="alldata">
                      <h6>Name: {user.name}</h6>
                      <h6>Email address: {user.email}</h6>
                      <h6>Password: {user.password}</h6>
                      <h6>Deposited amount: ${user.depositAmount}</h6>
                      <h6>Withdrawn amount: ${user.withdrawAmount}</h6>
                      <h6>Current balance: ${user.balance}</h6>  
                    </div>
                    </div>
                  )})
                  }
            </div>            
          </>
      }
    />
  );
}



export default AllData;
