import React, {useState} from "react";
import Card from "./Card";


function Deposit() {
  let [depositAmount, setDepositAmount] = useState(0.0);
  let [totalAmount, setTotalAmount] = useState(0.0);
  let [numberError, setNumberError] = useState("");

  //error string
  let numberErrorStr = "";
  //input limited to numbers
  const validate = (newValue) => {
    setNumberError("");
    if(!newValue.match(/^([(0-9)]+\.)*([(0-9)]+)*$/)) { //this regex matches numbers from 0 to 9 and + decimal numbers
      numberErrorStr = "Enter only numbers";
      setNumberError(numberErrorStr);
      return false; //didn't validate
    }
    return true; //did validate
  }

  const handleChange = e => {
    let newValue = e.target.value;
    if (validate(newValue) || !newValue) { //otherwise when we delete the numbers, it won't allow us to delete the last one
      setDepositAmount(newValue);
    }
  }

  //this also an event hadler; I could have wrapped this in a function that gets an event as argument
  const submitDeposit = currentValue => {
    totalAmount += parseFloat(currentValue);
    setTotalAmount(totalAmount);
  }


  return (
    <Card
        bgcolor="info"
        header="Deposit"
        // status={CreateAccount.status}
        body={
          <>  
            <h5 className="deposit mb-4">Skip extra trips to the bank and deposit your money online!</h5>
            <div>
              <div className="error">{numberError}</div>
            </div>

            <h6 className="deposit mb-4">Your current balance: ${totalAmount}</h6>

            <h6>The amount you wish to deposit:</h6>

            <input type="input" className="form-control" id="depositAmount" placeholder="Amount" onChange={handleChange}/>
            <br/>
            <button type="submit" disabled={!depositAmount} className="btn btn-light" onClick={() => submitDeposit(depositAmount)}>Submit</button>
          </>
        }
    />
  )
}

export default Deposit;
