import React from 'react';
import Card from "./Card";
import CreateAccount from "./CreateAccount";

function Login(){
  return (
    <Card
    bgcolor="primary"
    header="Create Account"
    status={status}
    body={show ? (  
        <>
            Name<br/>
            <input type="input" className="form-control" id="name" placeholder="Enter name" value={name} onChange={handleClick} /><br/>
            Email address<br/>
            <input type="input" className="form-control" id="email" placeholder="Enter email" value={email} onChange={handleClick}/><br/>
            Password<br/>
            <input type="password" className="form-control" id="password" placeholder="Enter password" value={password} onChange={handleClick}/><br/>
            <button type="submit" className="btn btn-light" onClick={handleCreate}>Create Account</button>
            </>
            ):(
            <>
            <h5>Success</h5>
            <button type="submit" className="btn btn-light" onClick={clearForm}>Add another account</button>
            </>
            )}
        />
  )  
}

export default Login;
