import React from 'react';
import Card from "./Card";
import MyContext from './MyContext';

/*

The Withdraw page includes a Bootstrap card with the following: 
- Withdraw input field on a Bootstrap card: ok
- Submit button on a Bootstrap card: ok
- Balance information displayed above the Withdrawal form: UI ok

Not done (check all functionality):
  - submit button function
  - current balance numbers
  - input validation
  - etc <3 

-
*/

function Withdraw() {
  const context = React.useContext(MyContext);



  return (
    <Card
        bgcolor="info"
        header="Withdraw"
        body={
            <>  
              <h5 className="deposit mb-4">Skip extra trips to the bank and withdraw your money online!</h5>
              <h6 className="balance mb-4">Your current balance is: ${context.balance}</h6>
              <h6>The amount you wish to withdraw:</h6>
              {/* <input type="input" className="form-control" id="email" placeholder="Amount" value={} onChange={}/> */}
              <div>
                <div className="error mb-3">{}</div>
              </div>
              <br/>
              {/* <button type="submit" disabled={!withdrawAmount} className="submitBtn btn btn-light" onClick={}>Withdraw</button> */}
              {}
            </>
        }
    />
  )
}

export default Withdraw;
