import React, {useState, createContext, useContext} from "react";
import Card from "./Card";

function CreateAccount() {
    const [show, setShow] = useState(true);
    const [status, setStatus] = useState("");
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const UserContext = createContext(null);
    const ctx = useContext(UserContext);  

    //input validation, sztem hianyos
    const validate = (fieldName, text) => {
        if (!fieldName) {
            setStatus('Error: ' + text);
            setTimeout(() => setStatus(""), 3000);
            return false;
        }
        return true;
    }

    //need to further check this
    const handleCreate = () => {
        // console.log(name, email, password);

        if (!validate(name, 'Please enter a name')) return;
        if (!validate(email, 'Please enter email address')) return;
        if (!validate(password, 'Please enter address')) return;
        ctx.users.push({name, email, password, balance:100});
        setShow(false);
    }    

    const handleNameChange = e => {
        setName(e.currentTarget.value);
    }

    const handleEmailChange = e => {
        setEmail(e.currentTarget.value);
    }

    const handlePasswordChange = e => {
        setPassword(e.currentTarget.value);
    }


    const clearForm = () => {
        setName("")
        setEmail("")
        setPassword("")
        setShow(true);
    }

    return (
        <Card
            bgcolor="info"
            header="Open Account"
            status={status}
            body={show ? (  
                <>
                    <h5 className="create mb-3">Open an account that fits your needs!</h5>
                    <h6>Name</h6>
                    <input type="input" className="form-control" id="name" placeholder="Enter name here" value={name} onChange={handleNameChange} /><br/>
                    <h6>Email address</h6>
                    <input type="input" className="form-control" id="email" placeholder="Enter email here" value={email} onChange={handleEmailChange}/><br/>
                    <h6>Password</h6>
                    <input type="password" className="form-control" id="password" placeholder="Enter password here" value={password} onChange={handlePasswordChange}/><br/>
                    <button type="submit" className="btn btn-light" onClick={handleCreate}>Create Account</button>
                    </>
                    ):(
                    <>
                    <h6>Success</h6>
                    <button type="submit" className="btn btn-light" onClick={clearForm}>Add another account</button>
                </>
            )}
        />
    )
}

export default CreateAccount;
