import React from "react";

function Card(props){
  console.log("props are: " + props)
    function classes() {
      const bg  = props.bgcolor ? ' bg-' + props.bgcolor : ' ';
      console.log("props are: " + props)

      const txt = props.txtcolor ? ' text-' + props.txtcolor: ' text-white';
      console.log("props are: " + props)

      return 'card mb-5 ' + bg + txt;
    }
    return (
      <div className={`cardContainer ${classes()}`}>
        <div className="card-header">{props.header}</div>
        {console.log("props are: " + props.header)}

        <div className="card-body mb-2">
          {props.title && (<h5 className="card-title mb-4">{props.title}</h5>)}
          {props.text && (<p className="card-text">{props.text}</p>)}
          {console.log("props are: " + props.title)}
          {console.log("props are: " + props.text)}
          {props.body}
          {console.log("props are: " + props.body)}
        </div>
      </div>      
    );    
  }

  export default Card;
