import React, {useState} from 'react';
// import Tooltip from 'react-tooltip';
// import "./index.css"

function NavBar() {

  let pages = [{text: "Create Acount", href:"#/CreateAccount/"},
               {text: "Login", href:"#/login/"},            
               {text: "Deposit", href:"#/deposit/"},
               {text: "Withdraw", href:"#/withdraw/"},
               {text: "Balance", href:"#/balance/"},
               {text: "All Data", href:"#/alldata/"},
  ];

  const [activePage, setActivePage] = useState(0);
  const handleClick = (e) => {
    setActivePage(e.currentTarget.dataset.index);
  }

  const getClass = (classIndex) => {
    if (classIndex == activePage) {
      return "current"
    }
    return ""
  }

  return (
    <>    
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <a className="navbar-brand" href="#">Welcome to Bad Bank</a>

      {/* BUTTON */}
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        {/* hamburger icon */}
        <span className="navbar-toggler-icon"></span> 
      </button>

      {/*  main container: containing nav items from item 1 till length of page */}
      <div className="collapse navbar-collapse nav-container" id="navbarNav">

        {/* container containing nav items only */}
        <ul className="navbar-nav navitem-container">
          {/* nav items: */}

          {pages.map((index) =>
            <li key={index} className="nav-item">
              <a data-index="1" className={`nav-link pl-2 pr-2 ${getClass(1)}`} href="#/login/" onClick={handleClick}>Login</a>
            </li>
          )}

          {/* <li className="nav-item">
            <a data-index="0" className={`nav-link pl-2 pr-2 ${getClass(0)}`} href="#/CreateAccount/" onClick={handleClick}>Create Account</a>
          </li>

          <li className="nav-item">
            <a data-index="1" className={`nav-link pl-2 pr-2 ${getClass(1)}`} href="#/login/" onClick={handleClick}>Login</a>
          </li>

          <li className="nav-item">
            <a data-index="2" className={`nav-link pl-2 pr-2 ${getClass(2)}`} href="#/deposit/" onClick={handleClick}>Deposit</a>
          </li>

          <li className="nav-item">
            <a data-index="3" className={`nav-link pl-2 pr-2 ${getClass(3)}`} href="#/withdraw/" onClick={handleClick}>Withdraw</a>
          </li>
          
          <li className="nav-item">
            <a data-index="4" className={`nav-link pl-2 pr-2 ${getClass(4)}`} href="#/balance/" onClick={handleClick}>Balance</a>
          </li>

          <li className="nav-item">
            <a data-index="5" className={`nav-link pl-2 pr-2 ${getClass(5)}`} href="#/alldata/" onClick={handleClick}>All Data</a>
          </li>           */}
        </ul>
        {/* end of container for nav items */}
      </div>
      {/* end of main container */}
    </nav>
    </>
  )
}


export default NavBar;
