import React, {useState} from "react";
import ReactTooltip from 'react-tooltip';

function NavBar() {
  
  /* Highlighting: The navigation bar highlights the element of the current page the user is on. */
  const [activePage, setActivePage] = useState(0);
  
  let pages = [{id:"home", text: "Welcome to Bad Bank", tooltipText: "Home Page", href:"#/"},
              {id:"createAccount", text: "Open Acount", tooltipText: "Open your Bad Bank速 account", href:"#/CreateAccount/"},
              {id:"login", text: "Login", tooltipText: "Log in to your Bad Bank速 profile", href:"#/login/"},            
              {id:"deposit",text: "Deposit", tooltipText: "Deposit cash & checks on any device", href:"#/deposit/"},
              {id:"withdraw",text: "Withdraw", tooltipText: "Withdraw cash & checks on any device", href:"#/withdraw/"},
              {id:"allData",text: "All Data", tooltipText: "No idea what comes here", href:"#/alldata/"},
  ];

  const handleClick = (e) => {
    setActivePage(e.currentTarget.dataset.index);
  }

  const getClass = (classIndex) => {

    if (window.performance) {
      if (performance.navigation.type == 1) {
        alert( "This page is reloaded" );
      } else {
        alert( "This page is NOT reloaded");
      }
    }    return classIndex == activePage ? "current" : "";
  }

  return (
    <>    
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      {/* <a className="navbar-brand" href="#">Welcome to Bad Bank</a> */}

      {/* BUTTON */}
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        {/* hamburger icon */}
        <span className="navbar-toggler-icon"></span> 
      </button>

      {/*  main container: containing nav items from item 1 till length of page */}
      <div className="collapse navbar-collapse nav-container" id="navbarNav">

        {/* container containing nav items only */}
        <ul className="navbar-nav navitem-container">

          {/* nav items: */}
          {/* Highlighting: The navigation bar highlights the element of the current page the user is on. */}
          {/* Tooltip implementation:
          When a user hovers their cursor over a navigation bar element, they see a few words describing that page. */}
          
          {pages.map((item, index) =>
            <li key={index} className="nav-item">
              <a data-tip data-for={item.id} data-index={index} className={`nav-link pl-2 pr-2 m-1 ${getClass(index)}`} href={item.href} onClick={handleClick}>{item.text}</a>
              <ReactTooltip id={item.id} effect='solid'>
                <span>{item.tooltipText}</span>
              </ReactTooltip>
              {/* data-for={item.id} and ReactTooltip id={item.id} need to be the same */}
            </li>
          )}
        </ul>
        {/* end of container for nav items */}
      </div>
      {/* end of main container */}
    </nav>
    </>
  )
}


export default NavBar;
