import React, {useState} from "react";
import Tooltip from "react-tooltip";
// import "react-tooltip/assets/bootstrap_white.css";

// let Tooltip = require("rc-tooltip");

function NavBar() {
  const [activePage, setActivePage] = useState();

  let pages = [{text: "Create Acount", href:"#/CreateAccount/"},
               {text: "Login", href:"#/login/"},            
               {text: "Deposit", href:"#/deposit/"},
               {text: "Withdraw", href:"#/withdraw/"},
               {text: "Balance", href:"#/balance/"},
               {text: "All Data", href:"#/alldata/"},
  ];

  const handleClick = (e) => {
    setActivePage(e.currentTarget.dataset.index);
  }

  const getClass = (classIndex) => {
    return classIndex == activePage ? "current" : "";
  }

  /* Tooltip implementation:
  When a user hovers their cursor over a navigation bar element, they see a few words describing that page.
  */

  const hoverText = <span>This is the text for Create Account</span>
  const styles = {
    display: 'table-cell',
    height: '60px',
    width: '80px',
    textAlign: 'center',
    background: '#f6f6f6',
    verticalAlign: 'middle',
    border: '5px solid white',
  };

  const rowStyle = {
    display: 'table-row',
  };

  return (
    <>    
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <a className="navbar-brand" href="#">Welcome to Bad Bank</a>

      {/* BUTTON */}
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        {/* hamburger icon */}
        <span className="navbar-toggler-icon"></span> 
      </button>

      {/*  main container: containing nav items from item 1 till length of page */}
      <div className="collapse navbar-collapse nav-container" id="navbarNav">

        {/* container containing nav items only */}
        <ul className="navbar-nav navitem-container">
          {/* nav items: */}
          <Tooltip
            placement="left"
            overlay={hoverText}
            style={rowStyle}
            arrowContent={
              pages.map((item, index) =>
            <li key={index} className="nav-item">
              <a data-index={index} className={`nav-link pl-2 pr-2 ${getClass(index)}`} href={item.href} onClick={handleClick}>{item.text}</a>
            </li>
          )}
          />





          {/* {pages.map((item, index) =>
            <li key={index} className="nav-item">
              <a data-index={index} className={`nav-link pl-2 pr-2 ${getClass(index)}`} href={item.href} onClick={handleClick}>{item.text}</a>
            </li>
          )} */}
        </ul>
        {/* end of container for nav items */}
      </div>
      {/* end of main container */}
    </nav>
    </>
  )
}


export default NavBar;
