import React, {useContext } from 'react';
import {MyContext} from "./MyContext";
import Card from "./Card";


function AllData() {

  const {users} = useContext(MyContext);

  return (
    <Card
      bgcolor="info"
      header="All Data"
      body={
          <>  
            <h5 className="balance mb-3">All Your Data in one spot!</h5>
            <div className="alldata">
            <div className="dataInfo mb-5">Your accounts and transactions</div>
              {users.map((user) => {
                  return (                   
                    <div className="dataInfo mb-4">Account:
                      <h6>Name: {user.name}</h6>
                      <h6>Email address: {user.email}</h6>
                      <h6>Password: {user.password}</h6>
                      <h6>Deposited amount: ${user.depositAmount}</h6>
                      <h6>Withdrawn amount: ${user.withdrawAmount}</h6>
                      <h6>Current balance: ${user.balance}</h6>  
                    </div>
                  )})
                  }
            </div>            
          </>
      }
    />
  );
}



export default AllData;
