import React, {useState} from "react";
import Card from "./Card";

function Deposit() {
  let [depositAmount, setDepositAmount] = useState(0.0);
  let [balance, setBalance] = useState(0.0);
  let [numberError, setNumberError] = useState("");
  let [successMessageVisible, setSuccessMessageVisible] = useState(false);

  //error string
  let numberErrorStr = "";
  //input limited to numbers
  const validate = newValue => {
    setNumberError("");
    if(!newValue.match(/^([(0-9)]+\.)*([(0-9)]+)*$/)) { //this regex matches numbers from 0 to 9 and + decimal numbers
      numberErrorStr = "Enter numbers only";
      setNumberError(numberErrorStr);
      return false; //didn't validate
    }
    return true; //did validate
  }

  const handleChange = e => {
    let newValue = e.target.value;
    if (validate(newValue) || !newValue) { //otherwise when we delete the numbers, it won't allow us to delete the last one
      setDepositAmount(newValue);
    } else {
      e.target.value = newValue.slice(0, -1); //when we delete the numbers, it cuts off the last element if that's not a number...right?
    } 
    setSuccessMessageVisible(false);   
  }

  //this also an event hadler; I could have wrapped this in a function that gets an event as argument
  const submitDeposit = currentValue => {
    balance += parseFloat(currentValue);
    setBalance(balance);
    setSuccessMessageVisible(true);
  }

  let showHideSuccessMessage = () => {
    if (successMessageVisible) {
      return (
        <div className="depositMsg mt-4">
          <h6>Your deposit was received!</h6>
        </div>
      )
    }
    return;
  }

  return (
    <Card
        bgcolor="info"
        header="Deposit"
        body= { (
          <>  
              <h5 className="deposit mb-4">Skip extra trips to the bank and deposit your money online!</h5>
              <h6 className="deposit mb-4">Your current balance is: ${balance}</h6>
              <h6>The amount you wish to deposit:</h6>
              <input type="input" className="form-control" id="depositAmount" placeholder="Amount" onChange={handleChange}/>
              <br/>
              <div>
                <div className="error mb-3">{numberError}</div>
              </div>
              <br/>
              <button type="submit" disabled={!depositAmount} className="submitBtn btn btn-light" onClick={() => submitDeposit(depositAmount)}>Deposit</button>            
              {showHideSuccessMessage()}
          </>
           )
        }  
    />
  )
}

export default Deposit;
