            <div className="alldata">
              {users.map((user) => {
                  return (                    
                    <div className="alldata">
                      <div className="title">Accounts created</div>
                      <h6>Name: {user.name}</h6>
                      <h6>Email address: {user.email}</h6>
                      <h6>Password: {user.password}</h6>
                      {/* <br />
                      <div className="title">Past Transactions</div>
                      <h6>Deposited amount: ${element.depositAmount}</h6>
                      <h6>Withdrawn amount: ${element.withdrawAmount}</h6>
                      <h6>Current balance: ${element.balance}</h6>  
                      */}
                    </div>
                  )})
                  }
            </div>            
          </>
      }
    />
  );
}



export default AllData;
