import React, {useState} from 'react';
import ReactDOM from 'react';
// import Tooltip from 'react-tooltip';
import "./index.css"

function NavBar() {
  let [currentTab, setCurrentTab] = useState(0)

  let handleClick = () => {
    // let myClass = document.getElementsByClassName("nav-link");
    // alert("hey");
    // return myClass.classList.add("current");

    // let myNewClass = document.getElementsByClassName("nav-link");
    // alert("hey");
    // myNewClass.className += " current";

  //   let current = 0;
  //   for (var i = 0; i < document.links.length; i++) {
  //     if (document.links[i].href === document.URL) {
  //       current = i;
  //     }
  //   }
  //   document.links[current].className = 'current';

    for (var i = 0; i < document.links.length; i++) {
      if (document.links[i].href === document.URL) {
      current = i;
      }
    }
    setCurrentTab("current")

  };

  return (
    <>
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <a className="navbar-brand" href="#">Welcome to Bad Bank</a>

      {/* BUTTON */}
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        {/* hamburger icon */}
        <span className="navbar-toggler-icon"></span> 
      </button>

      {/*  main container: containing nav items from item 1 till length of page */}
      <div className="collapse navbar-collapse nav-container" id="navbarNav">

        {/* container containing nav items only */}
        <ul className="navbar-nav navitem-container">

        {/* nav items: */}
          <li className="nav-item active">
            <a className="nav-link pl-2 pr-2" href="#/CreateAccount/" onClick={handleClick}>Create Account</a>
          </li>

          <li className="nav-item">
            <a className="nav-link pl-2 pr-2" href="#/login/" onClick={handleClick}>Login</a>
          </li>

          <li className="nav-item">
            <a className="nav-link pl-2 pr-2" href="#/deposit/" onClick={handleClick}>Deposit</a>
          </li>

          <li className="nav-item">
            <a className="nav-link pl-2 pr-2" href="#/withdraw/" onClick={handleClick}>Withdraw</a>
          </li>
          
          <li className="nav-item">
            <a className="nav-link pl-2 pr-2" href="#/balance/" onClick={handleClick}>Balance</a>
          </li>

          <li className="nav-item">
            <a className="nav-link pl-2 pr-2" href="#/alldata/" onClick={handleClick}>All Data</a>
          </li>          
        </ul>
        {/* end of container for nav items */}
      </div>
      {/* end of main container */}
    </nav>
    </>
  )
}


export default NavBar;
