  // console.log("this is context " + newContext);

  const {
    name,
    email,
    password,
    depositAmount,
    withdrawAmount,
    balance} = useContext(MyContext);


  // trying stuff -----------------------
  const data = [{
    name,
    email,
    password,
    depositAmount,
    withdrawAmount,
    balance
  }];

  // const array = data.map(values => {
