import React, {useContext } from 'react';
import {MyContext} from "./MyContext";
import Card from "./Card";

  {/* 
            
            I got an error that I need to use keys: Warning: Each child in a list should have a unique "key" prop.
            
            Pointed me to:
            https://reactjs.org/docs/lists-and-keys.html#keys
            
            -> Keys help React identify which items have changed, are added, or are removed. 
            Keys should be given to the elements inside the array to give the elements a stable identity:
            
            example:
            const numbers = [1, 2, 3, 4, 5];
            const listItems = numbers.map((number) =>
              <li key={number.toString()}>
                {number}
              </li>

            I gave one to each 3 but then deleted them because each time I create new data, it would need new keys
            and it kept giving me the error: 
            
            Warning: Encountered two children with the same key, `withdraw`. Keys should be unique so that components maintain their identity across updates. Non-unique keys may cause children to be duplicated and/or omitted — the behavior is unsupported and could change in a future version.
            );
             */}

function AllData() {

  const {users, depositArr, withdrawArr} = useContext(MyContext);
  let currentTime = new Date().toLocaleString();

  return (
    <Card
      bgcolor="info"
      header="All Data"
      body={
          <>  
            <h5 className="balance mb-3">All Your Data in one spot!</h5>
            <div className="alldata">
            <div className="dataInfo mb-4">Your accounts and transactions</div>
          

              {users.map((user) => {
                  return (                   
                    <div className="dataInfo mb-4">Account:
                      <h6>Creation date: {currentTime}</h6>
                      <h6>Name: {user.name}</h6>
                      <h6>Email address: {user.email}</h6>
                      <h6>Password: {user.password}</h6>
                    </div>
                  )})
                  }

                  {depositArr.map((amount) => {
                  return (                   
                    <div className="dataInfo mb-4">Transaction - Deposit:
                      <h6>Date: {currentTime}</h6>
                      <h6>Amount: ${amount.depositAmount}</h6>
                    </div>
                    )})
                  }

                  {withdrawArr.map((value) => {
                  return (                   
                    <div className="dataInfo mb-4">Transaction - Withdrawal:
                      <h6>Date: {currentTime}</h6>
                      <h6>Amount: ${value.withdrawAmount}</h6>
                    </div>
                    )})
                  }
            </div>            
          </>
      }
    />
  );
}



export default AllData;
