import React from 'react';
import Card from "./Card";
import CreateAccount from "./CreateAccount";

/*

The Deposit page contains a Bootstrap card form with the following: 
- Deposit input field: ok
- Deposit button: UI ok
- Balance information displayed above the Deposit form on the card

*/

function Deposit() {
  return (
    <Card
        bgcolor="primary"
        header="Deposit"
        status={CreateAccount.status}
        body={
            <>  <h5 className="balance mb-5">Your current balance: {}</h5>
                The amount you wish to deposit:<br/>
                <input type="input" className="form-control" id="email" placeholder="Amount" value={CreateAccount.email} onChange={CreateAccount.email}/>
                <br/>
                <button type="submit" className="btn btn-light" onClick={CreateAccount.email}>Submit</button>
            </>
        }
    />
  )
}

export default Deposit;
