import React, {useState, useContext} from "react";
import Card from "./Card";

function CreateAccount(){
    const [show, setShow]         = React.useState(true);
    const [status, setStatus]     = React.useState('');
    const [name, setName]         = React.useState('');
    const [email, setEmail]       = React.useState('');
    const [password, setPassword] = React.useState('');
    const ctx = React.useContext(UserContext);  

const validate = (field, label) => {
    if (!field) {
        setStatus('Error: ' + label);
        setTimeout(() => setStatus(''),3000);
        return false;
    }
    return true;
}

const handleCreate = () => {
    console.log(name,email,password);
    if (!validate(name,     'name'))     return;
    if (!validate(email,    'email'))    return;
    if (!validate(password, 'password')) return;
    ctx.users.push({name,email,password,balance:100});
    setShow(false);
}    

const handleClick = (e) => {
    setName(e.currentTarget.value)
    setEmail(e.currentTarget.value)
    setPassword(e.currentTarget.value);
}

const clearForm = () => {
    setName('');
    setEmail('');
    setPassword('');
    setShow(true);
}

return (
    <Card
    bgcolor="primary"
    header="Create Account"
    status={status}
    body={show ? (  
        <>
            Name<br/>
            <input type="input" className="form-control" id="name" placeholder="Enter name" value={name} onChange={handleClick} /><br/>
            Email address<br/>
            <input type="input" className="form-control" id="email" placeholder="Enter email" value={email} onChange={handleClick}/><br/>
            Password<br/>
            <input type="password" className="form-control" id="password" placeholder="Enter password" value={password} onChange={handleClick}/><br/>
            <button type="submit" className="btn btn-light" onClick={handleCreate}>Create Account</button>
            </>
            ):(
            <>
            <h5>Success</h5>
            <button type="submit" className="btn btn-light" onClick={clearForm}>Add another account</button>
            </>
            )}
        />
    )
  }

export default CreateAccount;
