import React, {useState, createContext, useContext} from "react";
import Card from "./Card";

function CreateAccount() {
    const [successMessageVisible, setSuccessMessageVisible] = useState(false); //instead of "show" initialized with true...
    // const [status, setStatus] = useState("");
    let [name, setName] = useState("");
    let [email, setEmail] = useState("");
    let [password, setPassword] = useState("");
    let [nameError, setNameError] = useState("");
    let [emailError, setEmailError] = useState("");
    let [passwError, setPasswError] = useState("");


    const MyContext = createContext(null); //creates a Context object
    const context = useContext(MyContext);  

    //I'll put error strings here so I don't have to declare it in the validateName() function
    let nameErrorStr = "";
    let emailErrorStr = "";
    let passwErrorStr = "";

    const validate = () => {
        //we need these to reset the values, it's buggy otherwise
        setNameError("");
        setEmailError("");
        setPasswError("");

        //error strings
        // let nameErrorStr = "";
        // let emailErrorStr = "";
        // let passwErrorStr = "";

        let emailFormat = /^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,4}$/; 
        //email format function
        let isValidFormat = str => {
            return str.match(emailFormat);
        }

        ////

        //EMPTY FIELDS (was required):
        if(!name) {
            nameErrorStr = "Oops! Your name can't be blank.";
        }

        if(!email) {
            emailErrorStr = "Oops! Your email can't be blank."
        }

        if(!password) {
            passwErrorStr = "Oops! Your password can't be blank."
        }

        //ADDITIONAL VALIDATION
        //name length (was not required)
        if(name.length < 4) {
            nameErrorStr = "Name must be longer than that."
        }

        //email format (was not required)
        if(!isValidFormat(email)) {
            emailErrorStr = "Oops! Invalid e-mail."
        }

        //password length (was required)
        if(password.length == 1 && password.length < 8) {
            passwErrorStr = "Password must be longer than that."
        }

        //error messages when inputs are left blank (was required)
        if(nameErrorStr || emailErrorStr || passwErrorStr) {
            setNameError(nameErrorStr);
            setEmailError(emailErrorStr);
            setPasswError(passwErrorStr);
            return false; //validation is false
        }
        return true; //validation is true
    }

    //limiting input to letters in the English alphabet
    const validateName = (newName) => {
        // let nameErrorStr = "";
        setNameError("");
        if(!newName.match(/^[a-z][a-z\s]*$/)) {
        nameErrorStr = "Enter only letters";
        setNameError(nameErrorStr);
        return false;
        }
        return true;
    }


    //validation on onClick / Create Account button
    const handleClick = e => {
            if(!validate()) { //if the validation is false, do all those things I wrote above :P
            return;
        }

        if(validate()) { //if the form validates:
            /*
            Requirements:
            - Success message: Upon selecting the Create Account button, the user should see a success message. 
            - Add another Account button: Upon selecting the Create Account button, the user should see an add another Account button. 
            - Cleared Create Account form: Upon selecting the Create Account button, it will open a cleared Create Account form.

            Reality:
            - Success message: Upon selecting the Create Account button, the user should see a success message. --> success message appears on card when all fields are completed correctly
            - Add another Account button: Upon selecting the Create Account button, the user should see an add another Account button. --> the new button appears on a new "page" after creating succesfully creating an account. there's only 1 button on the card / page
            - Cleared Create Account form: Upon selecting the Create Account button, it will open a cleared Create Account form. --> upon selecting the Create New Account button, we fire the clearForm() function which shows a clear form again
            */
            setSuccessMessageVisible(true);
        }

        //lenghty, my version, but the same as the given code below
        // if (!validate(name, 'Please enter name')) {
        //     return;
        // } else if(!validate(email, 'Please enter email address')) {
        //     return;
        // } else if(!validate(password, 'Please enter password')) {
        //     return;
        // }

        //short version / given code, it didn't return all 3 error messages, only name's error message
        // if (!validate(name, 'Please enter name')) return;
        // if (!validate(email, 'Please enter email address')) return;
        // if (!validate(password, 'Please enter password')) return;

        //dunno what this does, GOTTA GET BACK HERE
        // context.users.push({name, email, password, balance:100});
        // ez kell
        // context.users.push({name, email, password});
        // setShow(false);
    }    


    //form input 
    //can't have same function for all 3 as in the helper code I got (right?)
    const handleNameChange = e => {
        // setName(e.currentTarget.value); //if there's no letter validation
        let newName = e.currentTarget.value;
        if (validateName(newName) || !newName) {
        setName(newName);
        }
    }

    const handleEmailChange = e => {
        setEmail(e.currentTarget.value);
    }

    const handlePasswordChange = e => {
        setPassword(e.currentTarget.value);
    }


    const clearForm = () => {
        setName("");
        setEmail("");
        setPassword("");
        setSuccessMessageVisible(false);
    }

    return (
        <Card
            bgcolor="info"
            header="Open Account"
            // status={status}
            body={!successMessageVisible ? (  //if it's not visible, create account
                <>
                    <h5 className="create mb-3">Open an account that fits your needs!</h5>

                    <div>
                        <div className="error">{nameError}</div>
                        <div className="error">{emailError}</div>
                        <div className="error">{passwError}</div>
                    </div>

                    <h6>Name</h6>
                    <input type="input" className="form-control" id="name" placeholder="Your name is..." value={name} onChange={handleNameChange}/><br/>

                    <h6>Email address</h6>
                    <input type="input" className="form-control" id="email" placeholder="Your email address is..." value={email} onChange={handleEmailChange}/><br/>

                    <h6>Password</h6>
                    <input type="password" className="form-control" id="password" placeholder="Create a password" value={password} onChange={handlePasswordChange}/><br/>

                    {/* SUBMIT BUTTON */}
                    <button type="submit" disabled={!name && !email && !password} className="submitBtn btn btn-light" onClick={e => handleClick()}>Create Account</button>
                    </>
                    ):( //if it's visible, hide "previous page", show success msg and you can create a new account
                    <>
                    <div className="accountMsg mb-4">
                        <h6>Account succesfully created!</h6>
                    </div>
                    <button type="submit" className="submitBtn btn btn-light" onClick={clearForm}>Add another account</button>
                </>
            )}
        />
    )
}

export default CreateAccount;
