import React from 'react';
import Card from "./Card";
import CreateAccount from "./CreateAccount";

function Deposit() {

  const handleKeyPress = (source, event) => {
    let allowedChars = ".0123456789"
    let currentChar = event.key
    let found = false
    for (let i = 0;  i < allowedChars.length; i++) {
      if (currentChar === allowedChars[i]) {
        console.log("currentChar " + currentChar);
        console.log("allowedChars " + allowedChars);
        found = true
      }
    }
    if(found === false) {
      event.preventDefault()
      return
    }
  }

  return (
    <Card
        bgcolor="info"
        header="Deposit"
        status={CreateAccount.status}
        body={
          <>  
            <h5 class="deposit mb-4">Skip extra trips to the bank and deposit your money online!</h5>

            <h6 className="deposit mb-4">Your current balance: ${}</h6>

            <h6>The amount you wish to deposit:</h6>

            <input type="input" className="form-control" id="email" placeholder="Amount" value={CreateAccount.email} onChange={CreateAccount.email} onKeyPress={event => handleKeyPress(event)}/>
            <br/>
            <button type="submit" className="btn btn-light" onClick={CreateAccount.email}>Submit</button>
          </>
        }
    />
  )
}

export default Deposit;
