//I had to install and do all imports, none were given in the file

import React, {useState, useEffect, useReducer, Fragment} from "react";
import Axios from "axios";


//writing a custom hook
//takes 2 arguments
//returns 3 states and a function
const useDataApi = (initialUrl, initialData) => {
  const [url, setUrl] = useState(initialUrl);

  const [state, dispatch] = useReducer(dataFetchReducer, { //bundles together the states
    isLoading: false,                                      // dispatch is a function
    isError: false,
    data: initialData
  });

  // dispatch always takes an object, has 1 or 2 attributes, must have a type, but also a payload

  useEffect(() => {
    console.log("Here's useEffect()")
    let didCancel = false;
    const fetchData = async () => {
      dispatch({ type: "FETCH_INIT" });
      try {
        const result = await Axios(url);
        if (!didCancel) {
          dispatch({ type: "FETCH_SUCCESS", payload: result.data });
        }
      } catch (error) {
        if (!didCancel) {
          dispatch({ type: "FETCH_FAILURE" });
        }
      }
    };
    fetchData();
    return () => {
      didCancel = true;
    };
  }, [url]);
  return [state, setUrl];
};

const dataFetchReducer = (state, action) => {
  switch (action.type) {
    case "FETCH_INIT":
      return {
        ...state,
        isLoading: true, //overwriting isLoading and isError
        isError: false
      };
    case "FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false, //overwriting all 3 states
        isError: false,
        data: action.payload
      };
    case "FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true
      };
    default:
      throw new Error();
  }
};

function App() {
  const [query, setQuery] = useState("MIT");
  console.log("Here's App");

  //the custom hook
  ////returns 3 states and a function
  const [{ data, isLoading, isError }, doFetch] = useDataApi(
    "https://hn.algolia.com/api/v1/search?query=MIT",
    {
      hits: []
    }
  );

  return (
    <Fragment>
      <form
        onSubmit={event => {
          //this is setting the state here, setting the "url"
          doFetch(`http://hn.algolia.com/api/v1/search?query=${query}`);

          event.preventDefault();
        }}
      >
        <input
          type="text"
          value={query}
          onChange={event => setQuery(event.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      {isError && <div>Something went wrong ...</div>}

      {isLoading ? (
        <div>Loading ...</div>
      ) : (
        <ul>
          {data.hits.map(item => (
            <li key={item.objectID}>
              <a href={item.url}>{item.title}</a>
            </li>
          ))}
        </ul>
      )}
    </Fragment>
  );
}

// // ========================================
// ReactDOM.render(<App />, document.getElementById("root"));

export default App;
