//I had to install and do all imports, none were given in the file
import React, {useState, useEffect} from "react";
import Container from "react-bootstrap/Container";
import Axios from "axios";


function App() {
  // const { useState, useEffect } = React;
  // const { Container } = ReactBootstrap;
  const [data, setData] = useState({ hits: [] });
  const [url, setUrl] = useState("http://localhost:8080/data.json");
  const [isLoading, setIsLoading] = useState(false);
  const [query, setQuery] = useState("");

  console.log("Rendering App");

  /*
  - useEffect() doesn't cause a re-render
  - it's an asynchronous function
  - will only cause a re-render if we put something in the array here:

  fetchData();
  }, []); --> here

  */
  useEffect(() => { 
    console.log("Fetching data...");
    const fetchData = async () => {
      const result = await Axios(url); //Axios, not "axios"
      setData(result.data);
      setIsLoading(false);
    };

    fetchData();
  }, []);

  return (
    <Container>
      <input
        type="text"
        value={query}
        onChange={event => setQuery(event.target.value)}
      />
      <button
        type="button"
        onClick={() => setUrl("http://localhost:8080/data.json")}
      >
        Search
      </button>

      {isLoading ? (
        <div>Loading ...</div>
      ) : (
        <ul>
          {data.hits.map(item => (
            <li key={item.objectID}>
              <a href={item.url}>{item.title}</a>
            </li>
          ))}
        </ul>
      )}
    </Container>
  );
}

export default App;
