//I had to install and do all imports, none were given in the file
import React, {useState, useEffect} from "react";
import Container from "react-bootstrap/Container";
import Axios from "axios";

//getting data from HackerNews

function App() {
  // const { Container } = ReactBootstrap;
  // const { useState, useEffect } = React;

  const [data, setData] = useState({ hits: [] });
  const [query, setQuery] = useState("MIT");
  const [isError, setIsError] = useState(false);
  const [url, setUrl] = useState(
    "https://hn.algolia.com/api/v1/search?query=MIT"
  );
  const [isLoading, setIsLoading] = React.useState(false);
  console.log("Rendering App");
  
  useEffect(() => {   // Handles the LifeCycle Events
    console.log("Fetching data...");
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const result = await Axios(url);
        setData(result.data);
      } catch (error) {
        setIsError(true);
      }
      setIsLoading(false);
    };

    fetchData();
  }, [url]);

  //1. tracking the URL. if it changes, useEffect() changes & we're getting new re-renders
  //2. it fires and fetches the data again
  //so IRL in this app, the app re-rendeers on every input change
  
  return (
    <Container>
      <form
        onSubmit={event => {
          setUrl(`http://hn.algolia.com/api/v1/search?query=${query}`);

          event.preventDefault();
        }}
      >
        <input
          type="text"
          value={query}
          onChange={event => setQuery(event.target.value)}
        />
        <button type="submit">Search</button>
      </form>
      {isError && <div>Something went wrong ...</div>}

      {isLoading ? (
        <div>Loading ...</div>
      ) : (
        <ul>
          {data.hits.map(item => (
            <li key={item.objectID}>
              <a href={item.url}>{item.title}</a>
            </li>
          ))}
        </ul>
      )}
    </Container>
  );
}

export default App;
