import React from "react";

//new web component for the squares
//child component
const Square = ({id, player}) => { //picking up the properties. passing it in from the parent via the properties into the child
    //color palette
    const palet = ['red', 'blue', 'green'];
    //let's have a function that gives us a random color

    const randomColor = () => {
        let color = Math.random(palet);
        return color;
    }

    //making each of the blue squares a button
    return (
        //changing the color of Square on the click
        <button onClick={(e) => {
            // e.target.style.background = "red";
            {randomColor}
        }}>
            <h1>{id}</h1>
        </button>
    )
    //the ids appear on the button
}

//same thing as writing:

// const Square = (props) => {
//     return <button>{props.id}</button>
// }

//parent component
const Board = () => {
    const [player, setPlayer] = React.useState(1); //initialize player to 1
    let status = `Player ${player}`;

    //a function that creates the squares; square "factory"
    function renderSquare(i) { //*without the index argument, it doesn't work
        return <Square id={i}></Square>; //**this index
        //getting the value of **the index that's passed in as an argument for the function
        //id={i} is a property for Square
        //but how do we pick this up in the Square component itself? : with props!
    }

    return (
        <div
        className="game-board">
            {/* creating a grid row in which we'll put the blue squares */}
            <div className="grid-row">
            {/* *the numbers (arguments) can be the ids of the squares */}
                {renderSquare(0)} 
                {renderSquare(1)} 
                {renderSquare(2)} 

            </div>
        <div id="info">
            <h1>{status}</h1>
        </div>
        </div>
    );
};

export default Board;
