import React from "react";

//new web component for the squares
const Square = () => {
    //making each of the blue squares a button
    return <button></button>
}


const Board = () => {
    const [player, setPlayer] = React.useState(1); //initialize player to 1
    let status = `Player ${player}`;

    //a function that creates the squares; square "factory"
    function renderSquare(i) {
        return <Square></Square>;
    }

    return (
        <div
        className="game-board">
            {/* creating a grid row in which we'll put the blue squares */}
            <div className="grid-row">
                {renderSquare(0)}
            </div>
        <div id="info">
            <h1>{status}</h1>
        </div>
        </div>
    );
};

export default Board;
