/* ez a lecke: Video 15.4, 15.5

- az előzőbe videóba is volt szó state-ről, de ebbe a leckébe az első videóban teljesen ki lett hagyva
és megint kezdtük a HTML-es verzióval
- az előző state-es példába csak egy alert írta ki a totalt amúgy
*/

import React from "react";

const ATMDeposit = ({onChange}) => {
  return (
    <label className="label huge">
      Deposit:
      <input type="number" onChange={onChange}></input>
      {/* no idea what {onChange} is */}
      <input type="submit" value="submit"></input>  
    </label>
  );
};

// this will simulate the bank
// keeps track of the total amount of money
const Account = () => {
  let transactionState = 0; //how much money we're trying to withdraw or deposit
  const [totalState, setTotalState] = React.useState(0);
  let status = `Account Balance $ ${totalState}`;
  console.log("account rendered");

  const handleChange = event => {
    console.log(`handleChange ${event.target.value}`);
    transactionState = Number(event.target.value); 
  };

  //needs to get the event...in the video, it didn't and it still worked, wtf
  const handleSubmit = event => {
    setTotalState(totalState + transactionState); 
    // --> setting the state causes a re-render of the component, don't forget!
    event.preventDefault();
  };
  return(
    <form onSubmit={handleSubmit}>
      <h2 id="total">{status}</h2>
      <ATMDeposit onChange={handleChange}>Deposit</ATMDeposit>
    </form>
  );
};

export default Account;
