/* 

Now, you’ll learn how to set the player and pass the next player from the board to the square components through newState. 

- knowing the player that's playing the move, which is tracked by the Board
- we need to tell the child component who's playing the next tur n

*/

import React, {useState, useEffect} from "react";

//new web component for the squares
//child component
const Square = ({id, player, newState}) => { 
    const [color, setColor] = useState('green');

    // X or 0
    const [status, setStatus] = useState(null);
    const xo = ["0", "X"];                            //--> we're going to put either an X or an 0 into the status



    //color palette
    const palet = ['red', 'blue', 'green'];

    const getRandomColor = () => palet[Math.floor(Math.random() * 3)]; 

    /*
    - we change the state of each square now, and we want to track if they're being re-rendered (is React re-rendering)?
    - we can figure that out with useEffect()
    */

    useEffect(() => {
        console.log(`useEffect rendering square ${id}`); //will tell us which square is being re-rendered
        //when useEffect() is returning, we can pass it a function which will be called when it's unmounting the component
        return () => console.log(`useEffect unmounting square ${id}`) //I think this was the console.log I couldn't implement before :/
    });

    //--> every time we change the state, it's unmounting and then re-rendering
    //--> it's still a forced (by us, on purpose) unmounting/mounting causing React to re-render

    return (
    //making each of the blue squares a button
    //updated to new version of setColor() from the last video, Board8.js
        <button onClick={(e) => {
            let col = getRandomColor();
            setColor(col);
            //catching the next player (either an 0 or an X / a 0 or a 1)
            let nextPlayer = newState({id:id, color:col}); //takes this object and puts it in the array / stateOfGame variable of Board
            e.target.style.background = col;
        }}>
            <h1>{xo[status]}</h1>
        </button>
    )
}


//parent component
const Board10 = () => {
    //new prop: player
    const [player, setPlayer] = useState(1); //initialize player to 1
    console.log('Board (re-)rendering');

    //simulating the mount/unmount hook, I guess?
    const [mounted, setMounted] = useState(true);

    //this random nr will cause the re-render because the state has changed
    const [random, setRandom] = useState(0);

    //tracking a new state that is the total state of the game (I renamed it from state to stateOfGame)
    //as things are clicked, we're gonna add them to this empty array O.o
    const [stateOfGame, setStateOfGame] = useState([]);

    /*

    //NEW: we changed it a bit, we added a next player and set the nextPlayer's state, too:
    */

    // const newState = (obj) => {
    //     setStateOfGame([...stateOfGame, obj]); //we'll expand state and add to it the object
    //     console.log(`Adding state ${JSON.stringify(stateOfGame)}`)
    // }

    const newState = (obj) => {
        let nextPlayer = (player + 1) % 2;
        setPlayer(nextPlayer);
        setStateOfGame([...stateOfGame, obj]); //we'll expand state and add to it the object
        console.log(`Adding state ${JSON.stringify(stateOfGame)}`);
        status = `Player ${nextPlayer}`;
        return nextPlayer;
    }

    //now we'll need to call this function from our Square component (in renderSquare) / when clicking on the squares

    /////////////////////////////////

    let status = `Player ${player}`;

    //part of mount
    //1st click: unmounts, click again: mounts
    const toggle = () => setMounted(!mounted);
    const reRender = () => setRandom(Math.random());


    //a function that creates the squares; square "factory"
    function renderSquare(i) { //*without the index argument, it doesn't work
        return <Square id={i} player={player} newState={newState}></Square>; //adding the newState function here
    }
    return (
        <div
        className="game-board">
            {/* creating a grid row in which we'll put the blue squares */}
            <div className="grid-row">
            {/* *the numbers (arguments) can be the ids of the squares */}
            {/* we will need to be able to flip these on and off, stop the rendering, so let's simulate a component unmount hook*/}
            {/* if it's mounted, renderSquare will be called */}
                {mounted && renderSquare(0)}
                {mounted && renderSquare(1)}
                {mounted && renderSquare(2)}
            </div>
        <div id="info">
            <button onClick={toggle}>Show / hide row</button>
            {/* forcing a re-render, both parent (board) and child (squares) */}
            <button onClick={reRender}>Re-render</button>
            <h1>{status}</h1>
        </div>
        </div>
    );
};

export default Board10;
