import React from "react";
// TODO: import useFormik from formik library
import {useFormik} from "formik";


function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues: {
      email: "",
      password: ""
    },
    onSubmit: values => {
      alert("Login successful!");
    },
    validate: (values) => {
      let errors = {};
      if (!values.email) {
        errors.email = "Field required";
      }

      if (!values.password) {
        errors.password = "Field required";
      }
      return errors;
    },
  })


  return (
    <>
      <form onSubmit={formik.handleSubmit}>
        <label>Email</label>
        <input type="text" id="email-inp" name="email" onChange={formik.handleChange} value={formik.values.names}/>
        <label>Password</label>
        <input type="text" id="password-inp" name="password" onChange={formik.handleChange} value={formik.values.password}/>
        <button type="submit" id="submit-btn">Submit</button>
      </form>
    </>
  );
}

export default App;
