import React, {useState, useEffect} from "react";
import Movie from "./Movie";


function App() {
  const [data, setData] = useState(null);
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    async function getData() {
      const response = await fetch('./movies.json');
      const jsonFile = await response.json();
      setData(jsonFile);
      setLoaded(true);
    }
    getData();
  }, []);
  console.log('loaded:', loaded, 'data:', data);

  return (
    <>
      <div className="container">
        <div className="col-sm">

          {/* CARD WITH IMAGE OVERLAY*/}
          <div class="card">
            <div class="card bg-dark text-white">
              <img src="https://wallpapercave.com/wp/wp4016023.jpg" class="card-img" alt="deadpool" />
              <div class="card-img-overlay">
                <h5 class="card-title">Experience The Latest Movies</h5>
                <p class="card-text">Or just stream them on Netflix, that's fine, too.</p>
                <p class="card-text">...but we're going out of business, so please help.</p>
              </div>
            </div>
          </div>

          {loaded && data.movies.map((movie, i) => (
              // Step 3 - Rename '<nexttech-movie' to match the name of your new React Component in 'movies.js'
              <Movie
                // Do NOT remove this key attribute
                key={i}
                // Step 2 - Replace all of the attributes below with a single data={movie} attribute
                data={movie}
              />
            ))}
        </div>
      </div>
    </>
  );
}

export default App;
