import React from "react";

//refactored existing card
function Movie({ data }) {
  return (
    <>
      <div class="card text-center">
        <div class="card-header m-2.5">Movie Title</div>
        {/* CARD BODY */}
        <div class="card-body">
          <h5 className="movie-title">{data.title}</h5>
          <p class="card-text">{data.info}</p>
        </div>
        {/* CARD FOOTER */}
        <div class="card-footer text-muted">Date and Seating Information</div>
          <span>{data.showTime}</span>
          <label>Screen {data.screen}</label>
          <label>Row {data.row}</label>
          <label>Seat {data.seat}</label>
          <div>
          <a href="#" class="btn btn-primary">Buy Tickets</a>

          </div>
      </div>
    </>
  )
}


export default Movie;
