import React from "react";

function Movie({ data }) {
  // const movieStyle = {
  //   background: 'url(' + data.imageUrl + ')',
  //   backgroundSize: 'cover',
  // };
  return (
    <>
    <div class="card text-center">
      <div class="card-header">Movie</div>
      <div class="card-body">
        <h5 className="movie-title">{data.title}</h5>
        <p class="card-text">A cynical expatriate American cafe owner struggles to decide whether or not to 
        help his former lover and her fugitive husband escape the Nazis in French Morocco.</p>
        <a href="#" class="btn btn-primary">Go somewhere</a>
      </div>
      <div class="card-footer text-muted">2 days ago</div>
      <label>Date & Time</label>
      <span>${data.showTime}</span>
      <label>Screen</label>
      <span>{data.screen}</span>
        <label>Row</label>
      <span>{data.row}</span>
    </div>
    <div className="info-section">
      <label>Seat</label>
      <span>{data.seat}</span>
      </div>


</>
  )}


export default Movie;
